void ui_boot_screen_init(void)
{
ui_boot = lv_obj_create(NULL);
lv_obj_clear_flag( ui_boot, LV_OBJ_FLAG_SCROLLABLE );    /// Flags
lv_obj_set_style_bg_color(ui_boot, lv_color_hex(0xFFFFFF), LV_PART_MAIN | LV_STATE_DEFAULT );
lv_obj_set_style_bg_opa(ui_boot, 255, LV_PART_MAIN| LV_STATE_DEFAULT);

ui_loader = lv_spinner_create(ui_boot,1000,90);
lv_obj_set_width( ui_loader, 80);
lv_obj_set_height( ui_loader, 80);
lv_obj_set_x( ui_loader, 0 );
lv_obj_set_y( ui_loader, 142 );
lv_obj_set_align( ui_loader, LV_ALIGN_CENTER );
lv_obj_clear_flag( ui_loader, LV_OBJ_FLAG_CLICKABLE );    /// Flags
lv_obj_set_style_arc_color(ui_loader, lv_color_hex(0xFFFFFF), LV_PART_MAIN | LV_STATE_DEFAULT );
lv_obj_set_style_arc_opa(ui_loader, 0, LV_PART_MAIN| LV_STATE_DEFAULT);

ui_bootLogo = lv_img_create(ui_boot);
lv_img_set_src(ui_bootLogo, &ui_img_logo_panlee_png);
lv_obj_set_width( ui_bootLogo, LV_SIZE_CONTENT);  /// 1
lv_obj_set_height( ui_bootLogo, LV_SIZE_CONTENT);   /// 1
lv_obj_set_x( ui_bootLogo, -2 );
lv_obj_set_y( ui_bootLogo, -70 );
lv_obj_set_align( ui_bootLogo, LV_ALIGN_CENTER );
lv_obj_add_flag( ui_bootLogo, LV_OBJ_FLAG_ADV_HITTEST );   /// Flags
lv_obj_clear_flag( ui_bootLogo, LV_OBJ_FLAG_SCROLLABLE );    /// Flags

lv_obj_add_event_cb(ui_boot, ui_event_boot, LV_EVENT_ALL, NULL);

}
